import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import Pedidos from './Pedidos';
import Detalles from './Detalles';
import Login from './Login';
import { auth } from './firebase/firebaseConfig';

export default function App() {
  const [usuario, setUsuario] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
      // Forzar cierre de sesión en cada carga para mostrar siempre el login
    auth.signOut();
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUsuario(user);
      setLoading(false);
    });
    return () => unsubscribe();
  }, []);

  if (loading) return <div>Cargando...</div>;

 if (!usuario) return <Navigate to="/login" />;


  return (
    <Router>
      <nav className="navbar navbar-expand navbar-dark bg-dark px-3">
        <a className="navbar-brand" href="/">Ejemplo de frontend con React</a>
        <div className="navbar-nav">
          <Link className="nav-link" to="/pedidos">Pedidos</Link>
          <Link className="nav-link" to="/detalles">Detalles</Link>
        </div>
      </nav>
      <div className="container mt-4">
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/pedidos" element={<Pedidos />} />
          <Route path="/detalles" element={<Detalles />} />
          <Route path="/" element={<Navigate to="/pedidos" />} />
        </Routes>
      </div>
    </Router>
  );
}
