// src/App.js
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import Pedidos from './Pedidos';
import Detalles from './Detalles';
import Login from './Login';
import { auth } from './firebase/firebaseConfig';
import { signOut } from 'firebase/auth';

function Menu({ onLogout }) {
  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark mb-4">
      <div className="container">
        <Link className="navbar-brand" to="/pedidos">Mi App</Link>
        <div>
          <Link className="btn btn-outline-light me-2" to="/pedidos">Pedidos</Link>
          <Link className="btn btn-outline-light me-2" to="/detalles">Detalles</Link>
          <button className="btn btn-danger" onClick={onLogout}>Salir</button>
        </div>
      </div>
    </nav>
  );
}

function AppRoutes({ usuario, onLogout }) {
  return (
    <>
      {usuario && <Menu onLogout={onLogout} />}
      <Routes>
        <Route path="/" element={!usuario ? <Login /> : <Navigate to="/pedidos" />} />
        <Route path="/pedidos" element={usuario ? <Pedidos /> : <Navigate to="/" />} />
        <Route path="/detalles" element={usuario ? <Detalles /> : <Navigate to="/" />} />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </>
  );
}

export default function App() {
  const [usuario, setUsuario] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(async (user) => {
      if (user) {
        try {
          const token = await user.getIdToken();
          const decoded = JSON.parse(atob(token.split('.')[1]));
          const now = Math.floor(Date.now() / 1000);

          if (decoded.exp < now) {
            // Token expirado
            await signOut(auth);
            setUsuario(null);
            localStorage.removeItem('jwt');
          } else {
            localStorage.setItem('jwt', token);
            setUsuario(user);
          }
        } catch (err) {
          console.error('Error al validar token:', err);
          await signOut(auth);
          setUsuario(null);
          localStorage.removeItem('jwt');
        }
      } else {
        setUsuario(null);
        localStorage.removeItem('jwt');
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const handleLogout = async () => {
    await signOut(auth);
    setUsuario(null);
    localStorage.removeItem('jwt');
  };

  if (loading) return <div className="text-center mt-5">Cargando...</div>;

  return (
    <Router>
      <AppRoutes usuario={usuario} onLogout={handleLogout} />
    </Router>
  );
}
